css:
html,
body,
div,
span,
h1,
h2,
h3,
h4,
h5,
h6,
p {
  margin: 0 !important;
  padding: 0 !important;
  border: 0 ;
  font-size: 100% !important;
  font: inherit;
  vertical-align: baseline;
}
h3 {
  font-size: 2.5rem;
  font-weight: bold;
  color: white;
}
body {
  line-height: 1;
}

table {
  border-collapse: collapse;
  border-spacing: 0;
}
.head-first {
  background-color: #676767;
  width: 100%;
  height: 10vh;
  text-align: center;
  display: flex;
  justify-content: center;
  align-items: center;
  color : #fff;
}

.head-second {
  background-color: #1ac2d5;
  width: 100%;

  height: 10vh;
}
.personal {
  border-radius: 50%;
  width: 120px;
  border: 7px solid white !important;
}
.container {
  position: absolute;
  top: -2vh;
  left: 50%;
  transform: translateX(-50%);
  text-align: center;
  display: flex;
  justify-content: center;
  align-items: center;
  height: 15vh;
  text-align: center;
}

.employee-name {
  margin-top: 3vh !important;
  text-align: center;

  justify-content: center;
  align-items: center;
  text-align: center;
  font-family: Tahoma, sans-serif;
  font-size: 3rem;
  font-weight: 50;
  letter-spacing: 3px;
  color: black;
}

.last-name {
  font-weight: bold;
  margin-left: 20px;
  color: black;
}

.table-container {
  margin-top: 10vh !important;
  display: flex;

  justify-content: center;

  font-family: Tahoma, sans-serif;
}

.career {
  color: #1ac2d5;
  font-size: 1.6rem;
  margin-top: 5px;
}
tr {
  height: 5vh;
}

td {
  text-align: start;
  color: #1ac2d5;
  font-size: 20px !important;
}
td:nth-child(2) {
    direction: ltr !important;
  color: #676767;
  font-size: 16px !important;
}
.fotter-first {
    position:fixed;
    bottom:5vh;
    margin-top:10vh !important;
  background-color: #676767;
  width: 100%;

  height: 7vh;
    text-align: center;
  display: flex;
  justify-content: center;
  align-items: center;
}

.fotter-second {

       position:fixed;
    bottom:0px;
  background-color: #1ac2d5;
  width: 100%;

  height: 5vh;
}

.thub{
     border-radius: 50%;
  width: 30px;

}


.fotter-title{
    font-size: 2.5rem;
    color:white;

}
