css:
html,
body,
div,
span,
h1,
h2,
h3,
h4,
h5,
h6,
p {
  margin: 0  ;
  padding: 0  ;
  border: 0 !important ;
  font-size: 100% !important ;
  font: inherit !important ;
  vertical-align: baseline !important ;
}
body {
  line-height: 1 !important ;
  font-family: sans-serif !important ;
  letter-spacing: 3px !important ;
}


table {
  border-collapse: collapse !important ;
  border-spacing: 0 !important ;
}
.print-format{
     margin: 0 !important ;
  padding: 0 !important ;
}
.comp-head {
  margin: auto !important ;
  display: flex !important ;
  width: 90% !important ;
  text-align: center !important ;
  text-align: center !important ;
  justify-content: left !important ;
  align-items: center !important ;
   height: 50px !important;
   margin-bottom: 20px !important;
}

}
.comp-title {
  margin-left: 30px !important ;
  display: flex !important ;
  justify-content: left !important ;
  justify-items: left !important ;
  align-items: left !important ;
  flex-direction: column !important ;
     height: 50px !important;
}
.comp-title h2 {
  font-size: 2rem !important ;
  text-align: start !important ;
  font-weight: bold !important ;
}
.comp-title h4 {
  font-size: 1rem !important ;
  text-align: left !important ;
  font-weight: bold !important ;
}
.comp-head img {
  width: 50px !important ;
  font-weight: bold !important ;
  margin-right:20px !important;
}

/* invoic-headder */

.inv-head {
  margin: auto !important ;
  width: 100% !important ;
  margin-bottom: 30px !important ;
  display: flex !important ;

}

.invoice-head-left {
  background-color: #fac832 !important ;

  width: 55% !important ;
  height: 35px !important ;
  margin-right: 15px !important ;
  text-align: start !important ;
}
.inv-head h1 {
  line-height: 0.7 !important ;
  font-size: 2.7rem !important ;
  color: #474851 !important ;
  font-weight: 600 !important ;
}
.invoice-head-right {
  background-color: #fac832 !important ;
  width: 20% !important ;
  height: 35px !important ;
  margin-left: 15px !important ;
}

/* invoic-details */

.inv-details {
  margin: auto !important ;
  margin-left:2px !important;
  width: 90% !important ;
  margin-top: 10px !important ;
  display: flex !important ;
  height: 150px !important ;
  justify-content: space-between !important ;
  justify-items: left !important ;
  align-items: center !important ;
  margin-bottom:10px !important;
  margin-left:15px !important;
}

.inv-details-left {
  /*margin-right: 20px !important ;*/
  /*line-height: 1.5 !important ;*/
}
.inv-details-left h3 {
  font-size: 14px !important ;
  font-weight: bold !important ;
}
.inv-details img {
  width: 150px !important ;
  border: 2px solid black !important ;
}

.inv-details-right {
  /*margin-left: 50px !important ;*/
  text-align: end !important ;
  width: 30% !important ;
}

.inv-details-right td {
  text-align: start !important ;
  color: black !important ;
  font-size: 14px !important ;
  font-weight: bold !important ;
  line-height: 1.5 !important ;
}
.inv-details-right td:nth-child(2) {
  direction: ltr !important ;
  color: #474851 !important ;
  font-size: 12px !important ;
  text-align: end !important ;
}

/* invoic table style ---------------------------------------------------------------------------------- */
.inv-table {
  margin: auto !important ;
  padding:3px !important;

  width: 100% !important ;
  margin-top: 0px !important ;
  height: 500px !important ;
  border: 2px solid black !important ;
}
.inv-table thead {
  color: white !important ;
  background-color: #373b46 !important ;
}
.inv-table th {
  padding: 0.5rem !important ;
  text-align: center !important ;
}
.inv-table tbody {
  line-height: 2.5 !important ;
  font-weight: 500 !important ;
  font-family: serif !important ;
  text-align: center !important ;
}
.inv-table tbody tr:nth-child(odd) {
  background: white !important ;
}

.inv-table tbody tr:nth-child(even) {
  background: #eee !important ;
}

/* invoice summery style ---------------------------------------------------------------------------------- */
.inv-sum-container {
  margin: auto !important ;
  margin-top: 0px !important ;
  width: 90% !important ;
  display: flex !important ;
  /*height: 150px !important ;*/
  justify-content: space-between !important ;
  justify-items: left !important ;
  align-items: center !important ;
}
.inv-sum-left {
  line-height: 1.5 !important ;
}

.inv-sum-left p {
  font-size: 12px !important ;
}
.inv-sum-left h3 {
  font-weight: bold !important ;
}
.inv-sum-right {
  margin: auto !important ;
  display: flex !important ;
  flex-direction: column !important ;
  align-items: end !important ;
}
.inv-sum-right-top {
  text-align: right !important ;
  display: flex !important ;
  justify-content: end !important ;
}
.inv-sum-right-top td {
  width: 100px !important ;
  text-align: start !important ;
  color: black !important ;
  font-size: 16px !important ;
  font-weight: bold !important ;
  line-height: 1.5 !important ;
}
.inv-sum-right-top td:nth-child(2) {
  direction: ltr !important ;
  color: #474851 !important ;
  font-size: 14px !important ;
  text-align: end !important ;
}

.inv-sum-right-bottom {
  height: 40px !important ;
  width: 300px !important ;
  display: flex !important ;
  /* padding-left: 50px !important ; */
  background-color: #fac832 !important ;
  transform: translateX(15%) !important ;
  font-weight: bold !important ;
  font-size: 20px !important ;
}
.inv-sum-right-bottom td {
  text-align: center !important ;
}
.size-box {
  height: 30px !important ;
}
.size-box2 {
  height: 50px !important ;
}
